mapper.insert:

INSERT INTO community
(ctitle, ccontent, user_no2, fno, isNotice) 
VALUES (#{ctitle}, #{ccontent}, #{uno}, #{fno}, #{isNotice});

mapper.select:

SELECT uno,uid, uname, uemail, ukey, cno, ctitle, ccontent, cdate, cdel, fno, comment
FROM communityview 
WHERE cno = #{cno};


SELECT fno 
FROM files
ORDER BY fno DESC limit 1;

mapper.update:
		update community 
		set cdel = '0'
 		where cno = #{cno}

mapper.select:
SELECT fsn 
FROM files
WHERE fno = #{fno}
