mapper.select:
SELECT * 
FROM grprograms 
ORDER BY prg_no DESC;

mapper.update:
UPDATE grprograms 
SET prg_aprv = 1 
WHERE prg_no = #{prg_no}

UPDATE grschedule 
SET req_open = 1 
WHERE prg_no = #{prg_no}

UPDATE grprograms 
SET prg_aprv = 0 
WHERE prg_no = #{prg_no}

mapper.select:
SELECT prg_no, cns_no, prg_nm, prg_content, prg_nmtm, prg_nope, prg_schdl, prg_dow, prg_place 
FROM grprograms 
WHERE prg_no = #{no}

SELECT grsch_no 
FROM grschedule 
WHERE prg_no = #{prg_no}
