mapper.select:
SELECT grsch_no 
FROM grschedule 
WHERE prg_no = #{prg_no}

SELECT gaply_no
FROM graply
ORDER BY gaply_no DESC LIMIT 1;

SELECT cns_field
FROM counselor
WHERE cns_no = #{cns_no}

SELECT prg_content
FROM grprograms 
WHERE prg_no = #{no}

mapper.update:
UPDATE grprograms 
SET prg_aprv = 1 
WHERE prg_no = #{prg_no}

UPDATE grschedule 
SET req_open = #{req_open} 
WHERE prg_no = #{prg_no}

UPDATE grprograms 
SET prg_aprv = 0 
WHERE prg_no = #{prg_no}
